import React from 'react';
import "./BookCard.css";

const BookCard = (props) => {
    return (
        <div className="BookCard__card">
            <img src={props.image} alt="" />
            <div className="BookCard__cardtext">
                <h2>{props.title}</h2>
                <p>{turcate(props.description, 10)}</p>
            </div>
        </div>
    )
}

export default BookCard;
